{"metadata":{"kernelspec":{"name":"ir","display_name":"R","language":"R"},"language_info":{"name":"R","codemirror_mode":"r","pygments_lexer":"r","mimetype":"text/x-r-source","file_extension":".r","version":"4.0.5"}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"library(data.table)\nlibrary(dplyr)\nlibrary(mice)\n\ntrain <- fread(\"/kaggle/input/titanic/train.csv\")\n\nimp <- mice(train, m = 1, \n            method = c(\"\", \"\", \"\", \"\", \"\", \"pmm\", \"\", \"\", \"\",\"\", \"\", \"\"))\ntrain <- complete(imp)                       \ndensityplot(imp)\n\ntrain <- train %>% mutate(relateds=ifelse(train$SibSp > 0 | train$Parch > 0, 1, 0))\n\nlogit <- glm(data=train, Survived ~ as.factor(Sex)*relateds + Age*relateds + as.factor(Pclass) + SibSp, \n             family = binomial('logit'))\nsummary(logit)\n\ntrain <- train %>% mutate(pred=ifelse(logit$fitted.values >= 0.5, 1, 0))\ntrain <- train %>% mutate(acuracia=ifelse(train$Survived==train$pred, 1, 0))\nsum(train$acuracia)/891\n\ntest <- fread(\"/kaggle/input/titanic/test.csv\")\n\nimp2 <- mice(test, m = 1, \n            method = c(\"\", \"\", \"\", \"\", \"pmm\", \"\", \"\", \"\",\"\", \"\", \"\"))\ntest <- complete(imp2)                       \ndensityplot(imp2)\n\ntest <- test %>% mutate(relateds=ifelse(test$SibSp > 0 | test$Parch > 0, 1, 0))\npred <- predict(logit, newdata = test, type='response')\ntest <- test %>% mutate(Survived=ifelse(pred >= 0.5, 1, 0))\n\nfwrite(test[,c('PassengerId', 'Survived')], 'submission.csv')","metadata":{"execution":{"iopub.status.busy":"2022-05-30T20:37:31.704346Z","iopub.execute_input":"2022-05-30T20:37:31.735057Z","iopub.status.idle":"2022-05-30T20:37:32.3211Z"},"trusted":true},"execution_count":null,"outputs":[]}]}